<form action="" class="container installer-form" style="padding-top: 1.5rem; padding-bottom: 1rem" method="POST">
    <div class="row">
        <div class="col-12 col-md-6" style="padding-bottom: 1rem;">
            <label for="db_username" class="">Host</label>
            <input type="text"
                   class="form-control"
                   value="127.0.0.1"
                   name="db_username" id="db_host"
                   style="min-height: 18px; border-radius: 4px; ">
        </div>
        <div class="col-12 col-md-6" style="padding-bottom: 1rem;">
            <label for="db_username" class="">Database Username</label>
            <input type="text"
                   class="form-control"
                   name="db_username" id="db_username" style="min-height: 18px; border-radius: 4px; ">
        </div>

    </div>


</form>
